import React, { useState, useCallback } from 'react';
import Papa from 'papaparse';
import './App.css';

const App = () => {
  const [orderData, setOrderData] = useState([]);
  const [ratesData, setRatesData] = useState([]);
  const [results, setResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [uploadStatus, setUploadStatus] = useState({
    orders: false,
    rates: false
  });

  const DEFAULT_RATE = 60; // Default rate per kg if state not found

  // Parse CSV files
  const parseCSV = useCallback((file, callback) => {
    Papa.parse(file, {
      header: true,
      skipEmptyLines: true,
      complete: (results) => {
        if (results.errors.length > 0) {
          setError(`CSV parsing error: ${results.errors[0].message}`);
          return;
        }
        callback(results.data);
      },
      error: (error) => {
        setError(`Failed to parse CSV: ${error.message}`);
      }
    });
  }, []);

  // Handle order file upload
  const handleOrderUpload = useCallback((event) => {
    const file = event.target.files[0];
    if (!file) return;

    setError('');
    setLoading(true);

    parseCSV(file, (data) => {
      setOrderData(data);
      setUploadStatus(prev => ({ ...prev, orders: true }));
      setLoading(false);
    });
  }, [parseCSV]);

  // Handle rates file upload
  const handleRatesUpload = useCallback((event) => {
    const file = event.target.files[0];
    if (!file) return;

    setError('');
    setLoading(true);

    parseCSV(file, (data) => {
      setRatesData(data);
      setUploadStatus(prev => ({ ...prev, rates: true }));
      setLoading(false);
    });
  }, [parseCSV]);

  // Extract pack information from product name
  const extractPackInfo = (productName) => {
    const packOfOne = /Pack of 1/i.test(productName) ? 1 : 0;
    const packOfTwo = /Pack of 2/i.test(productName) ? 1 : 0;
    return { packOfOne, packOfTwo };
  };

  // Process orders and calculate shipping
  const processOrders = useCallback(() => {
    if (!orderData.length || !ratesData.length) {
      setError('Please upload both order and rates CSV files');
      return;
    }

    setLoading(true);
    setError('');

    try {
      // Create rates lookup
      const ratesLookup = {};
      ratesData.forEach(row => {
        const state = row.State?.trim().toUpperCase();
        const rate = parseFloat(row.Rate_per_kg);
        if (state && !isNaN(rate)) {
          ratesLookup[state] = rate;
        }
      });

      // Filter for shipped orders only
      const shippedOrders = orderData.filter(row => {
        const orderStatus = row['order-status']?.trim().toLowerCase();
        return orderStatus === 'shipped';
      });

      console.log(`Total orders: ${orderData.length}, Shipped orders: ${shippedOrders.length}`);

      // Group orders by Order ID
      const orderGroups = {};
      shippedOrders.forEach(row => {
        const orderId = row['amazon-order-id']?.trim();
        const productName = row['product-name']?.trim();
        const quantity = parseInt(row.quantity) || 1;
        const state = row['ship-state']?.trim().toUpperCase();

        if (!orderId || !productName) return;

        if (!orderGroups[orderId]) {
          orderGroups[orderId] = {
            orderId,
            state,
            packOfOne: 0,
            packOfTwo: 0,
            items: []
          };
        }

        const packInfo = extractPackInfo(productName);
        orderGroups[orderId].packOfOne += packInfo.packOfOne * quantity;
        orderGroups[orderId].packOfTwo += packInfo.packOfTwo * quantity;
        orderGroups[orderId].items.push({ productName, quantity, ...packInfo });
      });

      // Calculate shipping for each order
      const processedResults = Object.values(orderGroups).map(order => {
        const totalWeight = (order.packOfOne * 0.5) + (order.packOfTwo * 1.0);
        const roundedWeight = Math.ceil(totalWeight);
        const rate = ratesLookup[order.state] || DEFAULT_RATE;
        const shippingCost = roundedWeight * rate;

        return {
          orderId: order.orderId,
          state: order.state,
          packOfOne: order.packOfOne,
          packOfTwo: order.packOfTwo,
          totalWeight: totalWeight.toFixed(2),
          roundedWeight,
          ratePerKg: rate,
          shippingCost,
          stateFound: !!ratesLookup[order.state]
        };
      });

      setResults(processedResults);
      setLoading(false);
    } catch (err) {
      setError(`Processing error: ${err.message}`);
      setLoading(false);
    }
  }, [orderData, ratesData]);

  // Export results to CSV
  const exportResults = useCallback(() => {
    if (!results.length) return;

    const csvContent = Papa.unparse({
      fields: [
        'Order ID',
        'State',
        'Pack of One',
        'Pack of Two',
        'Total Weight (kg)',
        'Rounded Weight (kg)',
        'Rate per kg',
        'Shipping Cost'
      ],
      data: results.map(row => [
        row.orderId,
        row.state,
        row.packOfOne,
        row.packOfTwo,
        row.totalWeight,
        row.roundedWeight,
        row.ratePerKg,
        row.shippingCost
      ])
    });

    const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
    const link = document.createElement('a');
    link.href = URL.createObjectURL(blob);
    link.download = 'shipping_calculation_results.csv';
    link.click();
  }, [results]);

  // Calculate total shipping cost
  const totalShippingCost = results.reduce((sum, order) => sum + order.shippingCost, 0);

  return (
    <div className="app">
      <div className="container">
        <header className="header">
          <h1>🚚 Amazon Order Shipping Calculator</h1>
          <p>Upload your Amazon order report and state-wise shipping rates to calculate shipping costs</p>
        </header>

        {error && (
          <div className="error-message">
            <span>⚠️ {error}</span>
          </div>
        )}

        <div className="upload-section">
          <div className="upload-card">
            <h3>📋 Amazon Order Report</h3>
            <input
              type="file"
              accept=".csv"
              onChange={handleOrderUpload}
              disabled={loading}
              className="file-input"
            />
            <div className={`status ${uploadStatus.orders ? 'success' : 'pending'}`}>
              {uploadStatus.orders ? '✅ Uploaded' : '⏳ Pending'}
            </div>
            {orderData.length > 0 && (
              <p className="file-info">{orderData.length} orders loaded</p>
            )}
          </div>

          <div className="upload-card">
            <h3>💰 State-wise Shipping Rates</h3>
            <input
              type="file"
              accept=".csv"
              onChange={handleRatesUpload}
              disabled={loading}
              className="file-input"
            />
            <div className={`status ${uploadStatus.rates ? 'success' : 'pending'}`}>
              {uploadStatus.rates ? '✅ Uploaded' : '⏳ Pending'}
            </div>
            {ratesData.length > 0 && (
              <p className="file-info">{ratesData.length} states loaded</p>
            )}
          </div>
        </div>

        <div className="action-section">
          <button
            onClick={processOrders}
            disabled={!uploadStatus.orders || !uploadStatus.rates || loading}
            className="process-button"
          >
            {loading ? '⏳ Processing...' : '🧮 Calculate Shipping Costs'}
          </button>
        </div>

        {results.length > 0 && (
          <div className="results-section">
            <div className="results-header">
              <h2>📊 Shipping Calculation Results</h2>
              <button onClick={exportResults} className="export-button">
                📥 Export CSV
              </button>
            </div>

            <div className="table-container">
              <table className="results-table">
                <thead>
                  <tr>
                    <th>Order ID</th>
                    <th>State</th>
                    <th>Pack of One</th>
                    <th>Pack of Two</th>
                    <th>Total Weight (kg)</th>
                    <th>Rounded Weight (kg)</th>
                    <th>Rate per kg</th>
                    <th>Shipping Cost</th>
                  </tr>
                </thead>
                <tbody>
                  {results.map((row, index) => (
                    <tr key={index} className={!row.stateFound ? 'warning-row' : ''}>
                      <td>{row.orderId}</td>
                      <td>
                        {row.state}
                        {!row.stateFound && <span className="warning-badge">⚠️</span>}
                      </td>
                      <td>{row.packOfOne}</td>
                      <td>{row.packOfTwo}</td>
                      <td>{row.totalWeight}</td>
                      <td>{row.roundedWeight}</td>
                      <td>₹{row.ratePerKg}</td>
                      <td>₹{row.shippingCost}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>

            <div className="summary">
              <div className="summary-card">
                <h3>📈 Summary</h3>
                <div className="summary-stats">
                  <div className="stat">
                    <span className="stat-label">Total Orders:</span>
                    <span className="stat-value">{results.length}</span>
                  </div>
                  <div className="stat">
                    <span className="stat-label">Total Shipping Cost:</span>
                    <span className="stat-value">₹{totalShippingCost.toLocaleString()}</span>
                  </div>
                  <div className="stat">
                    <span className="stat-label">Average Cost per Order:</span>
                    <span className="stat-value">₹{Math.round(totalShippingCost / results.length)}</span>
                  </div>
                </div>
                {results.some(r => !r.stateFound) && (
                  <p className="warning-text">
                    ⚠️ Some states used default rate (₹{DEFAULT_RATE}/kg) - not found in rates file
                  </p>
                )}
              </div>
            </div>
          </div>
        )}

        <footer className="footer">
          <p>Built for efficient e-commerce shipping cost calculation</p>
        </footer>
      </div>
    </div>
  );
};

export default App;
